def getHolidays(hash)
  array = []
  newHash = {}
  array_of_holiday_hashes = []
  desc = ""
  hash.each do |key,value|
    value["response"].each do |key2, value2|
      value2.each do |key3, value3|
        if !array.include?(key3["name"] + key3["date"]["iso"][0..9])
          if key3["description"] == nil
            desc = "No description available"
          else
            desc = key3["description"]
          end
          array_of_holiday_hashes << {name: key3["name"], description: desc, date: key3["date"]["iso"][0..9], type: key3["type"][0]}
          array << key3["name"] + key3["date"]["iso"][0..9]
        end
      end
    end
  end
return array_of_holiday_hashes
end
